// client/src/pages/View.jsx

import React from 'react'
import Navbar from '../components/Navbar'
import useFetch from '../useFetch'
import {
    faCalendar,
    faMapLocationDot,
} from "@fortawesome/free-solid-svg-icons";
import {
    useLocation,
    useNavigate
} from "react-router-dom";
import {
    FontAwesomeIcon
} from "@fortawesome/react-fontawesome";
import "../styles/view.css"
import axios from "axios";

const View = () => {
    const location = useLocation();
    const id = location.pathname.split("/")[2];
    const { data } = useFetch(`/entries/${id}`)
    const navigate = useNavigate();

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:5500/api/entries/${data._id}`)
            navigate('/')
        } catch (err) {
            console.log(err)
        }
    };

    return (
        <div className='view'>
            <Navbar />
            <div className="postPageBG">
                <div className="upperContent">
                    <h1>{data.title}</h1>
                    <p>Date<FontAwesomeIcon className="icon"
                        icon={faCalendar} />:
                        {data.date}
                    </p>
                    <p>Location<FontAwesomeIcon className="icon"
                        icon={faMapLocationDot} />:
                        {data.location}
                    </p>
                </div>
            </div>

            <div className="postContainer">
                <div className="leftContainer">
                    {data.photos ? (
                        <div className="images">
                            <img 
                                src={data.photos[0]} 
                                height="200px" 
                                alt="" 
                            />
                        </div>
                    ) : (
                        "no Images"
                    )}
                </div>

                <div className="rightContainer">
                    <p>
                        " {data.text} "
                    </p>
                    <div className="button-group">
                        <button className="edit_button"
                            onClick={() => navigate(`/edit/${data._id}`)}>
                            Edit
                        </button>
                        <button className="del_button"
                            onClick={handleDelete}>
                            Delete
                        </button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default View
