// client/src/components/Card.jsx

import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/card.css";
import { faCircleArrowLeft, faCircleArrowRight } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Card(props) {
    const [currentImage, setCurrentImage] = useState(0);

    const handleMove = (direction) => {
        if (props.photos && props.photos.length > 1) {
            if (direction === "l") {
                setCurrentImage(prev => prev === 0 ? props.photos.length - 1 : prev - 1);
            } else {
                setCurrentImage(prev => prev === props.photos.length - 1 ? 0 : prev + 1);
            }
        }
    };

    return (
        <div className="card">
            <div className="content">
                <div className="image-container">
                    <img 
                        id="post-image" 
                        src={props.photos[currentImage]} 
                        alt="no content" 
                    />
                    {props.photos && props.photos.length > 1 && (
                        <>
                            <div className="arrows">
                                <FontAwesomeIcon
                                    icon={faCircleArrowLeft}
                                    className="arrow"
                                    onClick={() => handleMove("l")}
                                />
                                <FontAwesomeIcon
                                    icon={faCircleArrowRight}
                                    className="arrow"
                                    onClick={() => handleMove("r")}
                                />
                            </div>
                            <div className="image-count">
                                Image {currentImage + 1} of {props.photos.length}
                            </div>
                        </>
                    )}
                </div>
                <h4>{props.title}</h4>
                <h6>
                    <span>Date : </span> {props.date}
                </h6>
                <h6>
                    <span>Location : </span> {props.location}
                </h6>
                <p>{props.text.slice(0, 60)}...</p>
                <Link to={`view/${props._id}`}>
                    <button>Read More</button>
                </Link>
            </div>
        </div>
    );
}

export default Card;
