// client/src/pages/Create.jsx

import React, { useContext, useState } from 'react'
import axios from "axios"
import { AuthContext } from '../authContext';
import "../styles/create.css"
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { faPlusCircle, faCircleDown } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Create = () => {
    const navigate = useNavigate();
    const { user } = useContext(AuthContext);
    const [files, setFiles] = useState([]);
    const [info, setInfo] = useState({});
    const [expandedImage, setExpandedImage] = useState(null);
    const [isUploading, setIsUploading] = useState(false);

    const handleChange = (e) => {
        setInfo((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    }

    const handleFileChange = (e) => {
        const selectedFiles = Array.from(e.target.files);
        console.log("Selected files before check:", selectedFiles); // Debug log
        
        if (selectedFiles.length > 3) {
            alert("You can only upload up to 3 images");
            e.target.value = null;
            return;
        }

        // Validate file types
        const validTypes = ['image/jpeg', 'image/png', 'image/gif', 'image/webp'];
        const allValid = selectedFiles.every(file => validTypes.includes(file.type));
        
        if (!allValid) {
            alert("Please select only image files (JPEG, PNG, GIF, WEBP)");
            e.target.value = null;
            return;
        }

        console.log("Files being set:", selectedFiles); // Debug log
        setFiles(selectedFiles);
        setExpandedImage(null);
    }

    const toggleImage = (index) => {
        if (expandedImage === index) {
            setExpandedImage(null);
        } else {
            setExpandedImage(index);
        }
    }

    const uploadImage = async (file) => {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("upload_preset", "upload");
        
        const response = await axios.post(
            "https://api.cloudinary.com/v1_1/dmxzcafrn/image/upload",
            formData
        );
        
        return response.data.url;
    }

    const handleClick = async (e) => {
        e.preventDefault();
        
        if (!files.length) {
            alert("Please select at least one image");
            return;
        }

        try {
            setIsUploading(true);
            const imageUrls = [];

            // Upload images one by one
            for (let i = 0; i < files.length; i++) {
                const url = await uploadImage(files[i]);
                imageUrls.push(url);
            }

            const newEntry = {
                ...info,
                author: user._id,
                photos: imageUrls
            };

            const response = await axios.post(
                'http://localhost:5500/api/entries/',
                newEntry,
                { withCredentials: true }
            );

            navigate(`/view/${response?.data?._id}`);
        } catch (err) {
            console.error("Error:", err);
            alert("Failed to create entry. Please try again.");
        } finally {
            setIsUploading(false);
        }
    }

    return (
        <div className='create'>
            <Navbar />
            <div className="createContainer">
                <div className="picsContainer">
                    <div className="formInput">
                        <h2>Upload Images (Max 3)</h2>
                        <label htmlFor="file">
                            <FontAwesomeIcon
                                className="icon" 
                                icon={faPlusCircle}
                                style={{ cursor: 'pointer' }}
                            />
                        </label>
                        <input
                            type="file"
                            id="file"
                            name="file"
                            multiple={true}
                            onChange={handleFileChange}
                            style={{ display: "none" }}
                            accept="image/jpeg,image/png,image/gif,image/webp"
                        />
                    </div>
                    <div className="uploadedPictures">
                        {files.length > 0 && (
                            <div className="images-vertical">
                                {files.map((file, index) => (
                                    <div key={index} className="image-container">
                                        <img 
                                            src={URL.createObjectURL(file)} 
                                            alt={`Upload ${index + 1}`}
                                            className={expandedImage === index ? 'expanded' : ''}
                                            height="200px"
                                        />
                                        <FontAwesomeIcon
                                            icon={faCircleDown}
                                            className="expand-arrow"
                                            onClick={() => toggleImage(index)}
                                        />
                                        <div className="image-number">
                                            Image {index + 1} of 3 
                                            {files.length < 3 && ` (${3 - files.length} more allowed)`}
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                </div>

                <div className="input">
                    <label htmlFor="title">Title</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        id="title"
                        placeholder="Enter Title"
                        required
                    />
                </div>
                <div className="input">
                    <label htmlFor="location">Location</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        id="location"
                        placeholder="Enter Location"
                        required
                    />
                </div>

                <div className="input">
                    <label htmlFor="date">Date</label>
                    <input
                        onChange={handleChange}
                        type="date"
                        id="date"
                        placeholder="Choose Date"
                        required
                    />
                </div>

                <div className="input">
                    <label htmlFor="text">Write your thoughts..</label>
                    <textarea
                        name='text'
                        id='text'
                        cols="150"
                        rows='25'
                        onChange={handleChange}
                        required
                    ></textarea>
                </div>

                <button 
                    className='createBtn' 
                    onClick={handleClick}
                    disabled={isUploading}
                >
                    {isUploading ? 'Uploading...' : 'Create Entry'}
                </button>
            </div>
        </div>
    )
}

export default Create;
