import React, { useContext, useState, useEffect } from 'react'
import axios from "axios"
import { AuthContext } from '../authContext';
import "../styles/create.css"
import { useNavigate, useParams } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { faPlusCircle, faCircleArrowLeft, faCircleArrowRight } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import useFetch from '../useFetch';

const Edit = () => {
    const navigate = useNavigate();
    const { id } = useParams();
    const { user } = useContext(AuthContext);
    const [files, setFiles] = useState("");
    const [info, setInfo] = useState({});
    const { data } = useFetch(`/entries/${id}`);
    const [slideNumber, setSlideNumber] = useState(0);

    useEffect(() => {
        if (data) {
            setInfo({
                title: data.title,
                location: data.location,
                date: data.date,
                text: data.text
            });
        }
    }, [data]);

    const handleChange = (e) => {
        setInfo((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    }

    const handleMove = (direction) => {
        let newSlideNumber;
        let size = files ? Object.keys(files).length : (data?.photos?.length || 0);
        if (direction === "l") {
            newSlideNumber = slideNumber === 0 ? size - 1 : slideNumber - 1;
        } else {
            newSlideNumber = slideNumber === size - 1 ? 0 : slideNumber + 1;
        }
        setSlideNumber(newSlideNumber);
    }

    const handleClick = async (e) => {
        e.preventDefault();

        let updatedEntry = { ...info };

        if (files) {
            try {
                const list = await Promise.all(
                    Object.values(files).map(async (file) => {
                        const data = new FormData();
                        data.append("file", file);
                        data.append("upload_preset", "upload");
                        const uploadRes = await axios.post(
                            "https://api.cloudinary.com/v1_1/<your_cloudinary_key>/image/upload",
                            data,
                            { withCredentials: false }
                        );
                        return uploadRes.data.url;
                    })
                );

                updatedEntry = {
                    ...info,
                    photos: list
                };
            } catch (err) {
                console.error("Error uploading images:", err);
                alert("Failed to upload images");
                return;
            }
        }

        try {
            await axios.put(
                `http://localhost:5500/api/entries/${id}`,
                updatedEntry,
                { withCredentials: true }
            );
            navigate(`/view/${id}`);
        } catch (err) {
            console.error("Error updating entry:", err);
            alert("Failed to update entry");
        }
    }

    return (
        <div className='create'>
            <Navbar />
            <div className="createContainer">
                <div className="picsContainer">
                    <div className="formInput">
                        <h2>Upload New Images (Max 3)</h2>
                        <label htmlFor="file">
                            <FontAwesomeIcon
                                className="icon" icon={faPlusCircle} />
                        </label>
                        <input
                            type="file"
                            id="file"
                            multiple
                            onChange={(e) => setFiles(e.target.files)}
                            style={{ display: "none" }}
                            accept="image/*"
                            max="3"
                        />
                    </div>
                    <div className="uploadedPictures">
                        {files ? (
                            <div className="images">
                                <img 
                                    src={files[slideNumber] ? URL.createObjectURL(files[slideNumber]) : ""} 
                                    alt="" 
                                    height="300px" 
                                />
                                {Object.keys(files).length > 1 && (
                                    <div className="arrows">
                                        <FontAwesomeIcon
                                            icon={faCircleArrowLeft}
                                            className="arrow"
                                            onClick={() => handleMove("l")}
                                        />
                                        <FontAwesomeIcon
                                            icon={faCircleArrowRight}
                                            className="arrow"
                                            onClick={() => handleMove("r")}
                                        />
                                    </div>
                                )}
                            </div>
                        ) : (
                            data?.photos && (
                                <div className="images">
                                    <img 
                                        src={data.photos[slideNumber]} 
                                        alt="" 
                                        height="300px" 
                                    />
                                    {data.photos.length > 1 && (
                                        <div className="arrows">
                                            <FontAwesomeIcon
                                                icon={faCircleArrowLeft}
                                                className="arrow"
                                                onClick={() => handleMove("l")}
                                            />
                                            <FontAwesomeIcon
                                                icon={faCircleArrowRight}
                                                className="arrow"
                                                onClick={() => handleMove("r")}
                                            />
                                        </div>
                                    )}
                                </div>
                            )
                        )}
                    </div>
                </div>

                <div className="input">
                    <label htmlFor="title">Title</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        id="title"
                        value={info.title || ""}
                        placeholder="Enter Title"
                    />
                </div>
                <div className="input">
                    <label htmlFor="location">Location</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        id="location"
                        value={info.location || ""}
                        placeholder="Enter Location"
                    />
                </div>

                <div className="input">
                    <label htmlFor="date">Date</label>
                    <input
                        onChange={handleChange}
                        type="date"
                        id="date"
                        value={info.date || ""}
                        placeholder="Choose Date"
                    />
                </div>

                <div className="input">
                    <label htmlFor="text">Write your thoughts..</label>
                    <textarea
                        name='text'
                        id='text'
                        cols="150"
                        rows='25'
                        value={info.text || ""}
                        onChange={handleChange}
                    ></textarea>
                </div>

                <button className='createBtn' onClick={handleClick}>
                    Update Entry
                </button>
            </div>
        </div>
    )
}

export default Edit;
