// client/src/pages/Create.jsx

import React, { useContext, useState } from 'react'
import axios from "axios"
import { AuthContext } from '../authContext';
import "../styles/create.css"
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { faPlusCircle, faCircleArrowLeft, faCircleArrowRight } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Create = () => {
    const navigate = useNavigate();
    const { user } = useContext(AuthContext);
    const [files, setFiles] = useState([]);
    const [info, setInfo] = useState({});
    const [slideNumber, setSlideNumber] = useState(0);

    const handleChange = (e) => {
        setInfo((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    }

    const handleFileChange = (e) => {
        const selectedFiles = Array.from(e.target.files);
        if (selectedFiles.length > 3) {
            alert("You can only upload up to 3 images");
            e.target.value = null; // Reset the input
            return;
        }
        setFiles(selectedFiles);
        setSlideNumber(0); // Reset slide number when new files are selected
    }

    const handleMove = (direction) => {
        let newSlideNumber;
        let size = files.length;
        if (direction === "l") {
            newSlideNumber = slideNumber === 0 ? size - 1 : slideNumber - 1;
        } else {
            newSlideNumber = slideNumber === size - 1 ? 0 : slideNumber + 1;
        }
        setSlideNumber(newSlideNumber);
    }

    const handleClick = async (e) => {
        e.preventDefault();

        try {
            let newEntry = {
                ...info,
                author: user._id
            };

            if (files.length > 0) {
                const list = await Promise.all(
                    files.map(async (file) => {
                        const data = new FormData();
                        data.append("file", file);
                        data.append("upload_preset", "upload");
                        const uploadRes = await axios.post(
                            "https://api.cloudinary.com/v1_1/<your_cloudinary_key>/image/upload",
                            data,
                            { withCredentials: false }
                        );
                        return uploadRes.data.url;
                    })
                );
                newEntry.photos = list;
            }

            const response = await axios.post(
                'http://localhost:5500/api/entries/',
                newEntry,
                { withCredentials: true }
            );

            navigate(`/view/${response?.data?._id}`);
        } catch (err) {
            console.error("Error creating entry:", err);
            if (err.message.includes('upload')) {
                alert("Failed to upload images");
            } else {
                alert("Failed to create entry");
            }
        }
    }

    return (
        <div className='create'>
            <Navbar />
            <div className="createContainer">
                <div className="picsContainer">
                    <div className="formInput">
                        <h2>Upload Images (Max 3)</h2>
                        <label htmlFor="file">
                            <FontAwesomeIcon
                                className="icon" icon={faPlusCircle} />
                        </label>
                        <input
                            type="file"
                            id="file"
                            multiple
                            onChange={handleFileChange}
                            style={{ display: "none" }}
                            accept="image/*"
                            max="3"
                        />
                    </div>
                    <div className="uploadedPictures">
                        {files.length > 0 ? (
                            <div className="images">
                                <img 
                                    src={files[slideNumber] ? URL.createObjectURL(files[slideNumber]) : ""} 
                                    alt="" 
                                    height="300px" 
                                />
                                {files.length > 1 && (
                                    <div className="arrows">
                                        <FontAwesomeIcon
                                            icon={faCircleArrowLeft}
                                            className="arrow"
                                            onClick={() => handleMove("l")}
                                        />
                                        <FontAwesomeIcon
                                            icon={faCircleArrowRight}
                                            className="arrow"
                                            onClick={() => handleMove("r")}
                                        />
                                    </div>
                                )}
                            </div>
                        ) : null}
                    </div>
                </div>

                <div className="input">
                    <label htmlFor="title">Title</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        id="title"
                        placeholder="Enter Title"
                    />
                </div>
                <div className="input">
                    <label htmlFor="location">Location</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        id="location"
                        placeholder="Enter Location"
                    />
                </div>

                <div className="input">
                    <label htmlFor="date">Date</label>
                    <input
                        onChange={handleChange}
                        type="date"
                        id="date"
                        placeholder="Choose Date"
                    />
                </div>

                <div className="input">
                    <label htmlFor="text">Write your thoughts..</label>
                    <textarea
                        name='text'
                        id='text'
                        cols="150"
                        rows='25'
                        onChange={handleChange}
                    ></textarea>
                </div>

                <button className='createBtn' onClick={handleClick}>
                    Create Entry
                </button>
            </div>
        </div>
    )
}

export default Create;
