// client/src/pages/Create.jsx

import React, { useContext, useState } from 'react'
import axios from "axios"
import { AuthContext } from '../authContext';
import "../styles/create.css"
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { faPlusCircle, faCircleDown } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Create = () => {
    const navigate = useNavigate();
    const { user } = useContext(AuthContext);
    const [files, setFiles] = useState([]);
    const [info, setInfo] = useState({});
    const [expandedImage, setExpandedImage] = useState(null);

    const handleChange = (e) => {
        setInfo((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    }

    const handleFileChange = (e) => {
        const selectedFiles = Array.from(e.target.files);
        if (selectedFiles.length > 3) {
            alert("You can only upload up to 3 images");
            e.target.value = null; // Reset the input
            return;
        }
        setFiles(selectedFiles);
        setExpandedImage(null);
    }

    const toggleImage = (index) => {
        if (expandedImage === index) {
            setExpandedImage(null);
        } else {
            setExpandedImage(index);
        }
    }

    const handleClick = async (e) => {
        e.preventDefault();

        try {
            let newEntry = {
                ...info,
                author: user._id
            };

            if (files.length > 0) {
                const list = await Promise.all(
                    files.map(async (file) => {
                        const data = new FormData();
                        data.append("file", file);
                        data.append("upload_preset", "upload");
                        const uploadRes = await axios.post(
                            "https://api.cloudinary.com/v1_1/dmxzcafrn/image/upload",
                            data,
                            { withCredentials: false }
                        );
                        const { url } = uploadRes.data;
                        return url;
                    })
                );
                newEntry.photos = list;
            }

            const response = await axios.post(
                'http://localhost:5500/api/entries/',
                newEntry,
                { withCredentials: true }
            );

            navigate(`/view/${response?.data?._id}`);
        } catch (err) {
            console.error("Error creating entry:", err);
            if (err.message.includes('upload')) {
                alert("Failed to upload images");
            } else {
                alert("Failed to create entry");
            }
        }
    }

    return (
        <div className='create'>
            <Navbar />
            <div className="createContainer">
                <div className="picsContainer">
                    <div className="formInput">
                        <h2>Upload Images (Max 3)</h2>
                        <label htmlFor="file">
                            <FontAwesomeIcon
                                className="icon" icon={faPlusCircle} />
                        </label>
                        <input
                            type="file"
                            id="file"
                            multiple
                            onChange={handleFileChange}
                            style={{ display: "none" }}
                            accept="image/*"
                            max="3"
                        />
                    </div>
                    <div className="uploadedPictures">
                        {files.length > 0 && (
                            <div className="images-vertical">
                                {files.map((file, index) => (
                                    <div key={index} className="image-container">
                                        <img 
                                            src={URL.createObjectURL(file)} 
                                            alt={`Upload ${index + 1}`}
                                            className={expandedImage === index ? 'expanded' : ''}
                                            height="200px"
                                        />
                                        <FontAwesomeIcon
                                            icon={faCircleDown}
                                            className="expand-arrow"
                                            onClick={() => toggleImage(index)}
                                        />
                                        <div className="image-number">
                                            Image {index + 1} of 3 
                                            {files.length < 3 && ` (${3 - files.length} more allowed)`}
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                </div>

                <div className="input">
                    <label htmlFor="title">Title</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        id="title"
                        placeholder="Enter Title"
                    />
                </div>
                <div className="input">
                    <label htmlFor="location">Location</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        id="location"
                        placeholder="Enter Location"
                    />
                </div>

                <div className="input">
                    <label htmlFor="date">Date</label>
                    <input
                        onChange={handleChange}
                        type="date"
                        id="date"
                        placeholder="Choose Date"
                    />
                </div>

                <div className="input">
                    <label htmlFor="text">Write your thoughts..</label>
                    <textarea
                        name='text'
                        id='text'
                        cols="150"
                        rows='25'
                        onChange={handleChange}
                    ></textarea>
                </div>

                <button className='createBtn' onClick={handleClick}>
                    Create Entry
                </button>
            </div>
        </div>
    )
}

export default Create;
